'use client'

// src/label.tsx
import * as Slot from "@rn-primitives/slot";
import * as React from "react";
import { Pressable as Pressable2, Text as RNText } from "react-native";
var Root = React.forwardRef(({ asChild, ...props }, ref) => {
  const Component = asChild ? Slot.Pressable : Pressable2;
  return <Component ref={ref} {...props} />;
});
Root.displayName = "RootNativeLabel";
var Text2 = React.forwardRef(({ asChild, ...props }, ref) => {
  const Component = asChild ? Slot.Text : RNText;
  return <Component ref={ref} {...props} />;
});
Text2.displayName = "TextNativeLabel";
export {
  Root,
  Text2 as Text
};
