'use client'

// src/switch.tsx
import * as Slot from "@rn-primitives/slot";
import * as React from "react";
import { Pressable as Pressable2, View as View2 } from "react-native";
var Root = React.forwardRef(
  ({
    asChild,
    checked,
    onCheckedChange,
    disabled,
    onPress: onPressProp,
    "aria-valuetext": ariaValueText,
    ...props
  }, ref) => {
    function onPress(ev) {
      if (disabled) return;
      onCheckedChange(!checked);
      onPressProp?.(ev);
    }
    const Component = asChild ? Slot.Pressable : Pressable2;
    return <Component
      ref={ref}
      aria-disabled={disabled}
      role='switch'
      aria-checked={checked}
      aria-valuetext={ariaValueText ?? checked ? "on" : "off"}
      onPress={onPress}
      accessibilityState={{
        checked,
        disabled
      }}
      disabled={disabled}
      {...props}
    />;
  }
);
Root.displayName = "RootNativeSwitch";
var Thumb = React.forwardRef(({ asChild, ...props }, ref) => {
  const Component = asChild ? Slot.View : View2;
  return <Component ref={ref} role='presentation' {...props} />;
});
Thumb.displayName = "ThumbNativeSwitch";
export {
  Root,
  Thumb
};
